// CreateEvent.js
import React, { useState } from 'react';
import './CreateEvent.css'; // Import the CSS file for styling
import AxiosInstance from './axios';

const CreateEvent = () => {
  const defaultValues = {
    name: '',
    category: 'music',
    venue: '',
    description: '',
    startDate: '',
    endDate: '',
    startTime: '',
    endTime: '',
    tags: [],
    ticketType: 'free',
  };

  const [formData, setFormData] = useState({ ...defaultValues });

  const submission = (data) => {
    AxiosInstance.post('create-event/', {
      name: data.name,
      category: data.category,
      venue: data.venue,
      description: data.description,
      startDate: data.startDate,
      endDate: data.endDate,
      startTime: data.startTime,
      endTime: data.endTime,
      tags: data.tags,
      ticketType: data.ticketType,
    })
      .then((response) => {
        // Handle success (if needed)
        console.log('Event created successfully:', response.data);
      })
      .catch((error) => {
        // Handle error
        console.error('Error creating event:', error);
      });
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleTagInputChange = (e) => {
    const { value } = e.target;
    setFormData({ ...formData, tags: value.split(',').map((tag) => tag.trim()) });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    submission(formData);
  };

  const categories = ['music', 'art', 'fashion', 'education', 'theatre', 'standup', 'market', 'others'];

  return (
    <form onSubmit={handleSubmit} className="event-form">
      <label>
        Name:
        <input type="text" name="name" value={formData.name} onChange={handleInputChange} />
      </label>

      <label>
        Category:
        <select name="category" value={formData.category} onChange={handleInputChange}>
          {categories.map((cat) => (
            <option key={cat} value={cat}>
              {cat}
            </option>
          ))}
        </select>
      </label>

      <label>
        Venue:
        <input type="text" name="venue" value={formData.venue} onChange={handleInputChange} />
      </label>

      <label>
        Description:
        <textarea name="description" value={formData.description} onChange={handleInputChange} />
      </label>

      <label>
        Tags:
        <input type="text" name="tags" value={formData.tags.join(',')} onChange={handleTagInputChange} />
      </label>

      <div className="date-time-container">
        {/* ... (remaining date and time fields) */}
      </div>

      <div className="ticket-type-container">
        {/* ... (remaining ticket type fields) */}
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default CreateEvent;
