import React, { useState } from 'react';

const AppEventDetail = () => {
  const [events] = useState([
    { id: 1, name: 'Event 1', date: '2024-01-15', description: 'Description for Event 1' },
    { id: 2, name: 'Event 2', date: '2024-01-16', description: 'Description for Event 2' },
  ]);

  const [selectedEvent, setSelectedEvent] = useState(null);
  const [bookingName, setBookingName] = useState('');

  const handleEventClick = (event) => {
    setSelectedEvent(event);
  };

  const handleBookingNameChange = (e) => {
    setBookingName(e.target.value);
  };

  const handleBookEvent = () => {
    if (bookingName.trim() === '') {
      alert('Please enter your name before booking.');
      return;
    }

    alert(`Booking confirmed for ${bookingName} at ${selectedEvent.name}`);
  };

  return (
    <div>
      <h2>Events</h2>
      <ul>
        {events.map((event) => (
          <li key={event.id} onClick={() => handleEventClick(event)}>
            {event.name}
          </li>
        ))}
      </ul>

      {selectedEvent && (
        <div>
          <h2>{selectedEvent.name}</h2>
          <p>Date: {selectedEvent.date}</p>
          <p>Description: {selectedEvent.description}</p>

          <form>
            <label>
              Name:
              <input type="text" value={bookingName} onChange={handleBookingNameChange} />
            </label>
            <button type="button" onClick={handleBookEvent}>
              Book
            </button>
          </form>
        </div>
      )}
    </div>
  );
};

export default EventBookingPage;
