import React, { useState } from 'react';
// import './App.css';
import concertImage from 'src/assets/images'; // Replace with the actual path to your concert image

function AppEventDetails() {
  const [showDetails, setShowDetails] = useState(false);

  const eventDetails = (
    <div id="eventDetails" className={showDetails ? '' : 'hidden'}>
      <h2>Summer Jam 2024</h2>
      <img src={concertImage} alt="Concert" className="concertImage" />
      <p><strong>Date:</strong> July 20, 2024</p>
      <p><strong>Time:</strong> 7:00 PM - 11:00 PM</p>
      {/* ... rest of the details ... */}
    </div>
  );

  return (
    <div className="AppEventDetails">
      <h1>Concert Event Details</h1>
      <button onClick={() => setShowDetails(!showDetails)}>
        {showDetails ? 'Hide Event Details' : 'Show Event Details'}
      </button>
      {eventDetails}
    </div>
  );
}

export default AppEventDetails;
